<?php $wpttr_keys = json_decode(stripcslashes($_POST["hi_wptrr_pass_keys"])) ;global $wpdb;$id=$param['id'];$table_name = $wpdb->prefix . "trigger";$sql = "SELECT * FROM ".$table_name." where ID=$id"; $trigger_data = $wpdb->get_results($sql); //echo '<pre>';//print_r($trigger_data[0]);$objkey = "wptrr_" . $wptrr_num ;echo '<div class="wptrigger_contents">' ;	if($wpttr_keys->$objkey){				$keydata = array(						"wptrr_id" => $param['id'],						"trigger" => $wpttr_keys->$objkey,						"method" => "load"					) ;		$info = wptggFrontEnd::get_display_trigger_content($keydata) ;	                                		echo '<div class="wptrigger_content">' ;			if( $info["show_chk"] == "checked" ){				echo $info["display_txt"] ;			}else{				echo '<input type="text" class="wptgg_pass_key wptrigger" value="' . $wpttr_keys->$objkey . '" />						 <span class="wptgg_action">&nbsp;</span>											 <div class="wptrigger_append">' . $info["display_txt"] . '</div>' ;			}		echo '</div>' ;			}else{               		echo '<div class="wptrigger_contents">				<div class="wptrigger_content">';                                         					                                        if($trigger_data[0]->btnactivate=='on'):                                            echo '<input type="text" class="wptgg_pass_key1 wptrigger1" />';                                            echo '<input type="hidden" data-id="'.$id.'" class="id"/>';                                            if($trigger_data[0]->btn_value=='1'):                                                 if($trigger_data[0]->placement=='right'):                                                                                              echo  '<input type="submit" class="alignment" value="'.$trigger_data[0]->option.'"></input>';//                                                                                                else:                                                echo '</br></br>';                                                echo  '<input type="submit" class="alignment" value="'.$trigger_data[0]->option.'" />';                                            endif;                                                else:                                                     if($trigger_data[0]->placement=='right'):                                                                                              echo  '<input type="image" class="alignment" value="" src="'.$trigger_data[0]->option.'"" />';                                            else:                                                echo '</br></br>';                                                echo  '<input type="image" class="alignment" value="" src="'.$trigger_data[0]->option.'"" />';                                            endif;                                            endif;                                         else:                                            echo ' <input type="text" class="wptgg_pass_key wptrigger" /> ';                                        endif;					echo'<span class="wptgg_action">&nbsp;</span>									<div class="wptrigger_append"></div>                                                                                				</div>			</div>';	}	echo '<form class="wptgg_form" method="post" action="">				<input type="hidden" class = "hi_wptgg_id" value="' . $param['id'] . '" />					<input type="hidden" class = "hi_pass_key" value="' . $wpttr_keys->$objkey . '" />				<input type="hidden" class = "hi_wptgg_num" value="' .  $wptrr_num . '" />					<input type="hidden" name="" class="hi_wptrr_pass_keys" value="" />			</form>' ;echo '</div>' ;	?>